mapper.update:
UPDATE l
SET l_count = l_count + 1 
WHERE l_pcode = #{onc_code}

UPDATE l
SET l_count = l_count - 1 
WHERE l_pcode = #{onc_code}

mapper.insert:

	SELECT nvl( max(l_count),0) l_count from l
	where l_pcode = #{l_pcode}

INSERT INTO L VALUES('l_'||SEQ_L.NEXTVAL,#{l_count},1,#{l_m_email},#{l_pcode},#{l_cts_code})
